{
"ruleId": "discountForVIP",
"description": "Apply a 10% discount for VIP customers on their order",
"condition": {
"type": "AND",
"clauses": [
{
"field": "customer.status",
"operation": "EQUALS",
"value": "VIP"
},
{
"field": "order.total",
"operation": "GREATER_THAN",
"value": 100.00
}
]
},
"actions": [
{
"actionType": "DISCOUNT",
"discountPercent": 10
}
]
}
