{
  "ruleId": "discountForVIP",
  "description": "Apply a 10% discount for VIP customers on their order",
  "condition": {
    "type": "AND",
    "clauses": [
      {
        "field": "customer.status",
        "operation": "EQUALS",
        "value": "VIP"
      },
      {
        "field": "order.total",
        "operation": "GREATER_THAN",
        "value": 100.00
      }
    ]
  },
  "actions": [
    {
      "actionType": "DISCOUNT",
      "actionValue": 10
    }
  ]
},
  "actions": [
    {
      "actionType": "DISCOUNT",
      "actionValue": 10
    }
  ]
}

{
    "ruleId": "fraudDetection",
    "description": "Flag an order for review if the amount is unusually high",
    "condition": {
        "type": "OR",
        "clauses": [
            {
                "field": "order.amount",
                "operation": "GREATER_THAN",
                "value": 5000
            },
            {
                "field": "order.paymentMethod",
                "operation": "EQUALS",
                "value": "wire_transfer"
            }
        ]
    },
    "actions": [
        {
            "actionType": "FLAG_FOR_REVIEW",
            "reason": "Potential fraud detected"
        }
    ]
}

{
       "ruleId": "inventoryReplenishment",
       "description": "Replenish inventory when stock falls below the minimum level",
       "condition": {
           "type": null,
           "clauses": [
               {
                   "field": "product.stockLevel",
                   "operation": "LESS_THAN",
                   "value": 1000
               }
               
           ]
       },
       "actions": [
           {
               "actionType": "REPLENISH_STOCK",
               "membershipType": "PREMIUM"
           }
       ]
   }

{
    "ruleId": "membershipRenewal",
    "description": "Renew premium membership automatically if customer has enough",
    "condition": {
        "type": null,
        "clauses": [
            {
                "field": "customer.loyaltyPoints",
                "operation": "GREATER_THAN_EQUAL",
                "value": 1000
            }
            
        ]
    },
    "actions": [
        {
            "actionType": "RENEW_MEMBERSHIP",
            "membershipType": "PREMIUM"
        }
    ]
}

